require 'benchmark'

hash = ('a'..'z').each_with_object({}) { |char, hash| hash[char] = true }
range = ('a'..'z').freeze

test_chars = ""
test_chars = test_chars.chars

n = 50_000

Benchmark.bm do |bm|
  bm.report('Hash') do
    n.times do
      test_chars.each { |c| hash[c] }
    end
  end

  bm.report('Range') do
    n.times do
      test_chars.each { |c| range.include?(c) }
    end
  end
end
